// CSS File
import "./App.css";
// Styled-styledComponents
import Styled from "styled-components";
function App() {
  const Container = Styled.div`
    border: 5px solid black;
    width: 300px;
    height: 300px;
    padding: 5px 10px;
    margin: 20px auto;
    position: relative;
  `;
  const Circles = Styled.div`
    &:first-child,
    &:last-child{
        display: inline-block;
        background-color: yellow;
        width: 100px;
        height: 100px;
        border-radius: 50%;
        margin: 0 20px;
    }
    &:first-child span,
    &:last-child span{
      position: absolute;
      bottom: 10px;
      width: 80px;
      height: 26px;
      text-align: center;
      background-color: red;
      color: white;
      cursor: pointer;
      line-height: 1.5;
      border-radius: 6px;
    }
  `;
  return (
    <Container>
      <Circles>
        <span data-status="on">
          {document.querySelector("[data-status]")[0].dataset.status}
        </span>
      </Circles>
      <Circles>
        <span data-status="on"></span>
      </Circles>
    </Container>
  );
}

document.onclick = (e) => {
  if (e.target.id === "lamp") {
    alert("vfjdkl");
  }
};
export default App;
