// CSS File
import "./App.css";
// Styled-styledComponents
import Styled from "styled-components";
function App() {
  const Container = Styled.div`
    border: 5px solid black;
    width: 300px;
    height: 300px;
    padding: 5px 10px;
    margin: 20px auto;
    position: relative;
  `;
  const Circles = Styled.div`
    &:first-child,
    &:last-child{
        display: inline-block;
        background-color: yellow;
        width: 100px;
        height: 100px;
        border-radius: 50%;
        margin: 0 20px;
    }
    &:first-child span,
    &:last-child span{
      position: absolute;
      bottom: 10px;
      width: 80px;
      height: 26px;
      text-align: center;
      background-color: red;
      color: white;
      cursor: pointer;
      line-height: 1.5;
      border-radius: 6px;
      user-select: none;
    }
  `;
  return (
    <Container>
      <Circles>
        <span data-status="on"></span>
      </Circles>
      <Circles>
        <span data-status="on"></span>
      </Circles>
    </Container>
  );
}

// The Light Button
document
  .querySelectorAll("[data-status]")
  .map((ele) => (ele.innerHTML = ele.dataset.status));

// Light Effect
document.onclick = (e) => {
  if (e.target.dataset.status == "on") {
    e.target.dataset.status = "off";
    e.target.parentElement.style.backgroundColor = "black";
    e.target.innerHTML = e.target.dataset.status;
  } else {
    e.target.dataset.status = "on";
    e.target.parentElement.style.backgroundColor = "yellow";
    e.target.innerHTML = e.target.dataset.status;
  }
};
export default App;
